import { createContext, useContext, useState } from "react";

const cartContext = createContext([]);

export function useCartContext() {return useContext(cartContext)}


export function CartContextProvider({ children }) {

    const [cartList, setCartList] = useState([]);

    function agregarAlCarrito(item){
        // -1 no existe en el cart lis, 0 en adelante si is in cart
        const index = cartList.findIndex(prod => prod.item.id === item.item.id )

        if (index === -1) {
            // no existe, lo agrego
            setCartList( [ ...cartList, item ] )
        } else {
            // si existe
            const cant = cartList[index].cantidad
            cartList[index].cantidad = item.cantidad + cant 
            const newCartList = [ ...cartList ]
            setCartList(newCartList)
        }

        /*
        const evitarDuplicados = [...cartList]
        if (evitarDuplicados.some(i => i.item.id === item.item.id)) {
            evitarDuplicados.find(i => i.item.id === item.item.id).cantidad +=  item.cantidad
            setCartList(evitarDuplicados)
        } else {
            setCartList( [...cartList, item ] )
        }
        */
    }

    const cantidadCarrito = () => {
        return cartList.reduce((acum, prod) =>  acum += prod.cantidad  ,0)
    }

    const precioTotal = () => {
        return cartList.reduce((acum, prod) =>  acum= acum + (prod.item.precio * prod.cantidad)  ,0)
    }

    function eliminarDelCarrito( id ){

        setCartList( cartList.filter( i => i.item.id !== id ) )
    } 

    function vaciarCarrito(){
        setCartList([])
    }

    return <cartContext.Provider value={{
        cartList,
        agregarAlCarrito,
        cantidadCarrito,
        precioTotal,
        eliminarDelCarrito,
        vaciarCarrito
    }}>
            {children}
    </cartContext.Provider>;

};
