import { useCartContext } from '../../context/CartContext';
import '../../App.css'


export const Cart = () => {

  const { cartList, eliminarDelCarrito, vaciarCarrito } = useCartContext()

  return (
    <div className="contenedor">
      <h2>Carrito de compras:</h2> 
      { cartList.map(produ => 
      //<li>Producto: {produ.nombre} Precio: {produ.precio} Cantidad: {produ.cantidad}</li>
      <tr className="carrito">  
          <td>  
              <img src={produ.imagen} alt={produ.descripImagen}/>
          </td>
          <td>{produ.nombre}</td>
          <td>${produ.precio}</td>
          <td>${produ.cantidad}</td>
          <button onClick={eliminarDelCarrito}>X</button>
      </tr>
      
      )}
      <button onClick={vaciarCarrito}>Vaciar Carrito</button>
    </div>
)
};
