import { useCartContext } from '../../context/CartContext';
import { Link } from 'react-router-dom';
import { useState } from "react";
import { getFirestore, collection, addDoc, query, where, documentId, writeBatch, getDocs } from 'firebase/firestore'
import '../../App.css'


export const Cart = () => {

  const { cartList, totalPrice, removeFromCart, emptyCart } = useCartContext();

  const [ idOrder, setIdOrder ] = useState('')
  const [ dataForm, setDataForm ] = useState({
    email: '',
    phone: '',
    name: ''
  })


  const goShopping = async (e) => {
    e.preventDefault()  
         
    let orden = {}
       
    orden.buyer = dataForm;
    orden.items = cartList.map ( cartItem => {
        const id = cartItem.item.id;
        const name = cartItem.item.name;
        const price = cartItem.item.price * cartItem.quantity;
        const quantity = cartItem.quantity
        
        return {
            id, 
            name, 
            price, 
            quantity
        }   
    }) 
    orden.total = totalPrice(); 

    console.log(orden)
    
    const db = getFirestore()
    const ordersCollection = collection (db, 'orders')
    await addDoc( ordersCollection, orden)
    .then(resp => setIdOrder(resp.id))
    
    
    const queryCollection = collection(db, 'items')
        
    const queryUpdateStock = query(
        queryCollection, 
        where ( documentId() , 'in', cartList.map ( it => it.item.id ) )          
    ) 

    const batch = writeBatch(db)

    await getDocs(queryUpdateStock)
    .then(resp => resp.docs.forEach(res => batch.update(res.ref, {
            stock: res.data().stock - cartList.find(item => item.item.id === res.id).quantity
         })
    ))
    .catch(err => console.log(err))
    .finally(() => { 
      setDataForm({
        email: '',
        phone: '',
        name: ''
      })
      emptyCart();
    })    

    batch.commit()

  }

  const handleChange = (event) => {      
    setDataForm({ 
        ...dataForm,
        [event.target.name]: event.target.value
    })
  }



  return (
    <div className="container">
      {idOrder !== '' && `Muchas Gracias por su Compra!! El id de la orden es: ${idOrder}` }
      { cartList.length !== 0 ? <>
        { cartList.map(produ => 
        <div className="shoppingCart">  
          <div className="shoppingCart__image">
            <img src={produ.item.image} alt={produ.item.altImage}/>
          </div>
          <div className="shoppingCart__name">{produ.item.name}</div>
          <div className="shoppingCart__price">${produ.item.price}</div>
          <div className="shoppingCart__quantity">{produ.quantity}</div>
          <div className="shoppingCart__remove">
            <button className="remove-producto" onClick={() => removeFromCart(produ.item.id)}>x</button>
          </div>
        </div> 
        ) }
        <div className="shoppingCart__total">
          {`Total a Pagar: $${totalPrice()}`}
        </div>
        <button className="shoppingCart__button" onClick= { emptyCart } >Vaciar Carrito</button>
        <form onSubmit={goShopping}>
          <input 
            type='text' 
            name='name' 
            placeholder='Nombre' 
            onChange={handleChange}
            value={dataForm.name}
          />
        
          <input 
            type='number' 
            name='phone'
            placeholder='Teléfono' 
            onChange={handleChange}
            value={dataForm.phone}
          />
          
          <input 
            type='email' 
            name='email'
            placeholder='E-mail' 
            onChange={handleChange}
            value={dataForm.email}
          />

          <input 
            type='email' 
            name='validarEmail'
            placeholder='Repetir E-mail' 
            onChange={handleChange}
            //value={}
          />
          
          <button className="shoppingCart__button">Procesar Orden</button>
        </form>
        </>
      :   
        <>   
        <h2 className="shoppingCart__messageTitle">Tu carrito está ahora vacío!</h2>
        <p className="shoppingCart__message">Tenemos los mejores productos para tus mascotas, te invitamos a visitar nuestra tienda.</p>
        <Link to='/'>
          <button className="shoppingCart__button">Conoce Nuestros Productos</button>
        </Link>
        </> 
      }
    </div>
  )

};
