import React from 'react';
import ItemCount from '../ItemCount/ItemCount'


export const ItemDetail = ( {id, nombre, imagen, descripImagen, categoria, descripcion, precio, stock } ) => {

    function onAdd(cant){
        console.log(cant)
    }  
    
  return (
    <div className="detalles contenedor">
        
        <div key={id} className="detalle">
        <img className="detalle__imagen" src={imagen} alt={descripImagen}/>
            <div className="detalle__informacion"> 
                <h4>{nombre}</h4>  
                <p>Ferm√≠n {categoria}</p> 
                <p>{descripcion}</p>                                                            
                <p className="detalle__precio">${precio}</p>
                <ItemCount initial={1} stock={stock} onAdd={onAdd}/>   
            </div>
        </div>
        
    </div>
  

    )
};
