import React from 'react';
import '../../App.css'
import { useCartContext } from '../../context/CartContext';
import products from '../../helpers/mock';

export const Cart = () => {

  const { cartList } = useCartContext()

  return (
    <>
      <h2 className="contenedor">Carrito de compras</h2>; 
      { cartList.map(produ => <li>{produ.nombre} precio: {produ.precio} cantidad: {produ.cantidad}</li>)}
    </>
)
};
