import { createContext, useContext, useState } from "react";

const cartContext = createContext([]);

export function useCartContext() {return useContext(cartContext)}



export function CartContextProvider({ children }) {

    const [cartList, setCartList] = useState([]);

    function agregarAlCarrito(item){

         let previousCart = [...cartList]

        if (previousCart.some(i => i.id === item.id)) {
            
             previousCart.find(i => i.id === item.id).cantidad +=  item.cantidad
            setCartList(previousCart)
        } else {
            setCartList( [...cartList, item ] ) //simil Push
         }
    }

        /*
            const index = cartList.findIndex(i => i.item.id === item.id)//-1, pos
            console.log(index)
        
              if (index > -1) {
                const oldQy = cartList[index].cantidad
        
                cartList.splice(index, 1)
                setCartList([...cartList, { item, quantity: quantity + oldQy}])
              }
              else {
                setCartList([...cartList, {item, quantity}])
              }
          


    
       // return setCartList([ ...cartList, {item, cantidad} ]);
       
    }
    */

    

    function vaciarCarrito(){
        setCartList([])
    }

    return <cartContext.Provider value={{
        cartList,
        agregarAlCarrito,
        vaciarCarrito
    }}>
            {children}
    </cartContext.Provider>;

};
