import React from 'react';
import { Link } from 'react-router-dom';


export const Item = ( {prod} ) => {

    
  return (
    <div className="productos contenedor">
        <div key={prod.id} className="producto">
            <img className="producto__imagen" src={prod.imagen} alt={prod.descripImagen}/> 
            <div className="producto__informacion"> 
                <h4>{prod.nombre}</h4>  
                <p>Ferm√≠n {prod.categoria}</p>                                                            
                <div className="producto__precio">  
                    <p className="precio">${prod.precio}</p>  
                    <p className="oferta">${prod.oferta}</p> 
                </div>  
                <Link to={ `/detalle/${prod.id}` }>
                    <button className="producto__btnDetalleProducto">Detalle del Producto</button>
                </Link>
            </div>
        </div>
    </div>
  
    )
};
