import { useCartContext } from '../../context/CartContext';

export const CartList = () => {
    const { cartList, totalPrice, removeFromCart, emptyCart } = useCartContext();

    return (
        <>
        { cartList.map(produ => 
            <div className="shoppingCart">  
              <div className="shoppingCart__image">
                <img src={produ.item.image} alt={produ.item.altImage}/>
              </div>
              <div className="shoppingCart__name">{produ.item.name}</div>
              <div className="shoppingCart__price">${produ.item.price}</div>
              <div className="shoppingCart__quantity">{produ.quantity}</div>
              <div className="shoppingCart__remove">
                <button className="remove-producto" onClick={() => removeFromCart(produ.item.id)}>x</button>
              </div>
            </div> 
            ) }
            <div className="shoppingCart__total">
              {`Total a Pagar: $${totalPrice()}`}
            </div>
            <button className="shoppingCart__button" onClick= { emptyCart } >Vaciar Carrito</button>
        </>
    )
}
