import React from 'react';
import ItemCount from '../ItemCount/ItemCount'


export const ItemDetail = ({prod}) => {

    function onAdd(cant){
        console.log(cant)
    }  
    
  return (
    <div className="detalles contenedor">
        
        <div key={prod.id} className="detalle">
        <img className="detalle__imagen" src={prod.imagen} alt={prod.descripImagen}/>
            <div className="detalle__informacion"> 
                <h4>{prod.nombre}</h4>  
                <p>Ferm√≠n {prod.categoria}</p> 
                <p>{prod.descripcion}</p>                                                            
                <p className="detalle__precio">${prod.precio}</p>
                <ItemCount initial={1} stock={prod.stock} onAdd={onAdd}/>   
            </div>

        </div>
    </div>
  

    )
};
