import { createContext, useContext, useState } from "react";

const cartContext = createContext([]);

export function useCartContext() {return useContext(cartContext)}



export function CartContextProvider({ children }) {

    const [cartList, setCartList] = useState([]);

    function agregarAlCarrito(item, cantidad){


        
        if(evitarDuplicados(item)) {
			const cambiarCantidad = [...cartList]
			cambiarCantidad.forEach(x => {
				if(x.id === item ) {
					x.cantidad += 1;
				}
			})
			return setCartList(cambiarCantidad);
		}
        return setCartList([ ...cartList, {item, cantidad} ]);
    }
    
    function evitarDuplicados(item){
		const buscarItem = cartList.find((i) => {
			return i.id === item.id;
		})
		return buscarItem;
    }
    

    function vaciarCarrito(){
        setCartList([])
    }

    return <cartContext.Provider value={{
        cartList,
        agregarAlCarrito,
        vaciarCarrito
    }}>
            {children}
    </cartContext.Provider>;

};
