import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { useCartContext } from '../../context/CartContext';
import ItemCount from '../ItemCount/ItemCount'


export const ItemDetail = ( { product } ) => {  

    const { cartList, addToCart } = useCartContext();

    const [count, setCount] = useState (0);

    function onAdd ( quantity ) {
        addToCart ( {item: product, quantity: quantity} )
        setCount ( quantity )
    }  
    
    console.log(cartList)

  return (
    <>
    <div className="details container">    
        <div key={product.id} className="detail">
            <img className="detail__image" src={product.imagen} alt={product.descripImagen}/>
            <div className="detail__information"> 
                <h4>{product.nombre}</h4>  
                <p>Ferm√≠n {product.categoria}</p> 
                <p>{product.descripcion}</p>                                                            
                <p className="detail__price">${product.precio}</p>  
            {
                count === 0 ? 
                    <ItemCount initial={1} stock={product.stock} onAdd={onAdd}/>
                :
				    <>
                        <p className="detail__message">Producto agregado al Carrito!</p>
					    <Link to='/cart'>
                            <button className="product__button">Terminar compra</button>
                        </Link>
					    <Link to='/'>
                            <button className="product__button">Continuar comprando</button>
                        </Link>
				    </>
		    } 
            </div>
        </div> 
    </div>
    </>
  
    )
};
