import React from 'react';
import { Link } from 'react-router-dom';


export const Item = ( {id, name, image, altImage, category, price, noOffer} ) => {

    
  return (
        <div key={id} className="product">
            <img className="product__image" src={image} alt={altImage}/> 
            <div className="product__information"> 
                <h4>{name}</h4>  
                <p>Ferm√≠n {category}</p>                                                            
                <div className="product__price">  
                    <p className="price">${price}</p>  
                    <p className="discount">${noOffer}</p> 
                </div>  
                <Link to={ `/detail/${id}` }>
                    <button className="product__button">Detalle del Producto</button>
                </Link>
            </div>
        </div>  
    )
};
