import React from 'react'
import { useEffect, useState } from "react"
import { getFetch } from "../../productos/mock"


export const ItemListContainer = () => {
    
    const [productos, setProductos] = useState ([])
    const [loading, setloading] = useState(true)
        
        
    useEffect(() => {
        // llamada a una api. Tarea asincónica
        getFetch
        .then(res => setProductos(res))
        .catch(err => console.log(err))
        //.then(respuesta => console.log(respuesta))
        .finally(()=> setloading(false))   
        //console.log('api')     
    }, [])
    
    return (
        <div>
            <h1>Bienvenidos a Fermín Pet Shop!</h1>
            
        
            { loading ? <h2>Cargando ...</h2> :
                                    productos.map( prod => 
                                        <div key={prod.id} className="producto">
                                            <img className="producto__imagen" src={prod.imagen} alt={prod.descripImagen}/> 
                                            <div className="producto__informacion"> 
                                                <h4>{prod.nombre}</h4>  
                                                <p>Fermín {prod.categoria}</p>  
                                                <label>Cantidad:</label>
                                                <input type="number" min="1" value="1" value={prod.cantidad}></input>
                                                <div className="producto__precio">  
                                                    <p className="precio">${prod.precio}</p>  
                                                    <p className="oferta">${prod.oferta}</p> 
                                                </div>  
                                            </div>
                                        </div>
                                                                       
            
            ) }           
            

        </div>
    )
}
